'use client';

import { motion } from 'framer-motion';
import * as React from 'react';

type ContentIllustrationProps = {
  className?: string;
  once?: boolean;
  startDelay?: number;
  delayStep?: number;
  x?: number;
  duration?: number;
  inViewAmount?: number | 'some' | 'all';
};

const slideFrom = (
  dir: 'left' | 'right',
  delay = 0,
  x = 60,
  duration = 0.6,
) => ({
  hidden: { opacity: 0, x: dir === 'left' ? -x : x },
  show: {
    opacity: 1,
    x: 0,
    transition: { duration, ease: [0.22, 1, 0.36, 1], delay },
  },
});

export default function ContentIllustration({
  className = '',
  once = true,
  startDelay = 0,
  delayStep = 0.2,
  x = 60,
  duration = 0.6,
  inViewAmount = 0.7,
}: ContentIllustrationProps) {
  return (
    <motion.svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 560 450"
      role="img"
      aria-hidden="true"
      className={className}
    >
      <style>{`.s0 { fill: #fafafa }`}</style>

      <defs>
        <image
          width="299"
          height="174"
          id="f1"
          href="data:image/png;base64,"
        />
        <image
          width="267"
          height="74"
          id="f2"
          href="data:image/png;base64,"
        />
        <image
          width="268"
          height="73"
          id="f3"
          href="data:image/png;base64,"
        />
      </defs>

      {/* background panel */}
      <path
        fillRule="evenodd"
        className="s0"
        d="M146 0h268c8.84 0 16 7.16 16 16v418c0 8.84-7.16 16-16 16H146c-8.84 0-16-7.16-16-16V16c0-8.84 7.16-16 16-16z"
      />

      {/* cards */}
      <motion.use
        id="f1"
        href="#f1"
        x={41}
        y={46}
        initial="hidden"
        whileInView="show"
        viewport={{ once, amount: inViewAmount }}
        variants={slideFrom('left', startDelay + 0 * delayStep, x, duration)}
      />
      <motion.use
        id="f2"
        href="#f2"
        x={253}
        y={240}
        initial="hidden"
        whileInView="show"
        viewport={{ once, amount: inViewAmount }}
        variants={slideFrom('right', startDelay + 1 * delayStep, x, duration)}
      />
      <motion.use
        id="f3"
        href="#f3"
        x={88}
        y={332}
        initial="hidden"
        whileInView="show"
        viewport={{ once, amount: inViewAmount }}
        variants={slideFrom('left', startDelay + 2 * delayStep, x, duration)}
      />
    </motion.svg>
  );
}
